import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";

interface ProductDetailsProps {
  brand: {
    name: string;
    displayName: string;
    description: string;
    metadata?: any;
  };
}

export function ProductDetails({ brand }: ProductDetailsProps) {
  // Enhanced product data for Fruitful brand
  const getProductData = () => {
    if (brand.name === 'FRUITFUL') {
      return {
        brandPhilosophy: "If you don't like the fruits you are growing, change the seeds...",
        brandColors: ['#4ECDC4', '#FFE66D', '#FF6B6B'],
        brandValues: ['Growth Mindset', 'Natural Development', 'Positive Transformation'],
        targetAudience: 'Individuals and organizations focused on personal and professional growth',
        keyFeatures: [
          'Inspirational Brand Philosophy',
          'Growth-Oriented Messaging',
          'Positive Life Transformation',
          'Mindset Development Tools'
        ],
        brandAssets: [
          {
            type: 'Logo',
            description: 'High-resolution Fruitful™ logo with vibrant colors',
            available: true
          },
          {
            type: 'Thank You Asset',
            description: 'Professional thank you image with brand representative',
            available: true
          }
        ],
        usageGuidelines: [
          'Maintain brand philosophy in all communications',
          'Use approved color palette for consistency',
          'Focus on growth and transformation messaging',
          'Respect trademark usage requirements'
        ]
      };
    }

    if (brand.name === 'LIONS_SEEDWAVE') {
      return {
        brandPhilosophy: "Strategic brand sovereignty through analytical precision",
        specialization: "Advanced Brand Bloodline Sovereignty Analysis",
        targetMarket: "Sovereign-tier enterprises requiring premium strategic consulting",
        brandColors: ['#1E7B3D', '#FFFFFF', '#000000'],
        keyCapabilities: [
          'Brand Bloodline Analysis',
          'Sovereignty Metrics Development',
          'Strategic Market Positioning',
          'Premium Consulting Services'
        ],
        brandAssets: [
          {
            type: 'Logo',
            description: 'Professional Seedwave logo with green circular design',
            available: true
          }
        ]
      };
    }

    if (brand.name === 'WATER_THE_SEED') {
      return {
        brandPhilosophy: "Active Growth Protocol & Brand Development System",
        focus: "Systematic brand development and nurturing",
        keyFeatures: [
          'Growth Analytics Dashboard',
          'Brand Development Tracking',
          'Active Protocol Implementation',
          'Performance Monitoring'
        ]
      };
    }

    // Default metadata display
    return brand.metadata || {};
  };

  const productData = getProductData();

  return (
    <Card className="mt-6">
      <CardHeader>
        <CardTitle className="flex items-center space-x-2">
          <i className="fas fa-cube text-blue-600"></i>
          <span>Product Details</span>
        </CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-6">
          {/* Brand Philosophy */}
          {productData.brandPhilosophy && (
            <div>
              <h4 className="font-semibold text-gray-900 mb-2">Brand Philosophy</h4>
              <p className="text-gray-600 italic">"{productData.brandPhilosophy}"</p>
            </div>
          )}

          {/* Brand Colors */}
          {productData.brandColors && (
            <div>
              <h4 className="font-semibold text-gray-900 mb-2">Brand Colors</h4>
              <div className="flex space-x-2">
                {productData.brandColors.map((color: string, index: number) => (
                  <div key={index} className="flex flex-col items-center">
                    <div 
                      className="w-12 h-12 rounded-lg border shadow-sm"
                      style={{ backgroundColor: color }}
                    ></div>
                    <span className="text-xs text-gray-500 mt-1">{color}</span>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Key Features */}
          {productData.keyFeatures && (
            <div>
              <h4 className="font-semibold text-gray-900 mb-2">Key Features</h4>
              <div className="grid grid-cols-1 gap-2">
                {productData.keyFeatures.map((feature: string, index: number) => (
                  <div key={index} className="flex items-center space-x-2">
                    <i className="fas fa-star text-yellow-500 text-sm"></i>
                    <span className="text-gray-600">{feature}</span>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Brand Values */}
          {productData.brandValues && (
            <div>
              <h4 className="font-semibold text-gray-900 mb-2">Brand Values</h4>
              <div className="flex flex-wrap gap-2">
                {productData.brandValues.map((value: string, index: number) => (
                  <Badge key={index} variant="secondary">{value}</Badge>
                ))}
              </div>
            </div>
          )}

          {/* Target Audience */}
          {productData.targetAudience && (
            <div>
              <h4 className="font-semibold text-gray-900 mb-2">Target Audience</h4>
              <p className="text-gray-600">{productData.targetAudience}</p>
            </div>
          )}

          {/* Brand Assets */}
          {productData.brandAssets && (
            <div>
              <h4 className="font-semibold text-gray-900 mb-2">Available Brand Assets</h4>
              <div className="space-y-3">
                {productData.brandAssets.map((asset: any, index: number) => (
                  <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <div>
                      <div className="font-medium text-gray-900">{asset.type}</div>
                      <div className="text-sm text-gray-600">{asset.description}</div>
                    </div>
                    <Badge variant={asset.available ? "default" : "secondary"}>
                      {asset.available ? "Available" : "Pending"}
                    </Badge>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Usage Guidelines */}
          {productData.usageGuidelines && (
            <div>
              <h4 className="font-semibold text-gray-900 mb-2">Usage Guidelines</h4>
              <div className="space-y-2">
                {productData.usageGuidelines.map((guideline: string, index: number) => (
                  <div key={index} className="flex items-start space-x-2">
                    <i className="fas fa-check-circle text-green-500 text-sm mt-0.5"></i>
                    <span className="text-gray-600 text-sm">{guideline}</span>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

server/storage.ts.content:
import {
  users,
  organizations,
  brands,
  licenses,
  brandMetrics,
  systemSettings,
  type User,
  type UpsertUser,
  type Organization,
  type InsertOrganization,
  type Brand,
  type InsertBrand,
  type License,
  type InsertLicense,
  type BrandMetrics,
  type SystemSetting,
  BRAND_TIERS,
  GEOGRAPHIC_DIVISIONS,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, gte, lte, count, sum, sql, inArray } from "drizzle-orm";

export interface IStorage {
  // User operations (mandatory for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Organization operations
  createOrganization(org: InsertOrganization): Promise<Organization>;
  getOrganization(id: string): Promise<Organization | undefined>;
  getUserOrganization(userId: string): Promise<Organization | undefined>;

  // Brand operations
  getAllBrands(filters?: {
    tier?: string[];
    division?: string[];
    search?: string;
    limit?: number;
    offset?: number;
  }): Promise<{ brands: Brand[]; total: number }>;
  getBrand(id: string): Promise<Brand | undefined>;
  createBrand(brand: InsertBrand): Promise<Brand>;
  updateBrand(id: string, updates: Partial<InsertBrand>): Promise<Brand>;

  // License operations
  createLicense(license: InsertLicense): Promise<License>;
  getLicense(id: string): Promise<License | undefined>;
  getUserLicenses(userId: string): Promise<License[]>;
  getOrganizationLicenses(orgId: string): Promise<License[]>;
  getBrandLicenses(brandId: string): Promise<License[]>;

  // Analytics operations
  getDashboardMetrics(): Promise<{
    totalRevenue: string;
    activeLicenses: number;
    newBrands72h: number;
    complianceRate: string;
    tierDistribution: Record<string, number>;
    revenueHistory: Array<{ date: string; revenue: number }>;
  }>;

  // System settings
  getSystemSetting(key: string): Promise<SystemSetting | undefined>;
  setSystemSetting(key: string, value: string, type: string, description?: string): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Organization operations
  async createOrganization(orgData: InsertOrganization): Promise<Organization> {
    const [organization] = await db
      .insert(organizations)
      .values(orgData)
      .returning();
    return organization;
  }

  async getOrganization(id: string): Promise<Organization | undefined> {
    const [organization] = await db
      .select()
      .from(organizations)
      .where(eq(organizations.id, id));
    return organization;
  }

  async getUserOrganization(userId: string): Promise<Organization | undefined> {
    const user = await this.getUser(userId);
    if (!user?.organizationId) return undefined;
    return this.getOrganization(user.organizationId);
  }

  // Brand operations
  async getAllBrands(filters?: {
    tier?: string[];
    division?: string[];
    search?: string;
    limit?: number;
    offset?: number;
  }): Promise<{ brands: Brand[]; total: number }> {
    const conditions = [eq(brands.isActive, true)];

    if (filters?.tier?.length) {
      conditions.push(inArray(brands.tier, filters.tier));
    }

    if (filters?.division?.length) {
      conditions.push(inArray(brands.geographicDivision, filters.division));
    }

    if (filters?.search) {
      conditions.push(
        sql`${brands.name} ILIKE ${`%${filters.search}%`} OR ${brands.displayName} ILIKE ${`%${filters.search}%`}`
      );
    }

    const whereClause = and(...conditions);

    // Build count query
    const countQuery = db.select({ count: count() }).from(brands).where(whereClause);

    // Build main query with all chaining at once
    let query = db.select().from(brands).where(whereClause).orderBy(desc(brands.createdAt));

    if (filters?.limit) {
      query = query.limit(filters.limit) as typeof query;
    }

    if (filters?.offset) {
      query = query.offset(filters.offset) as typeof query;
    }

    const [brandsResult, countResult] = await Promise.all([
      query,
      countQuery
    ]);

    return {
      brands: brandsResult,
      total: countResult[0]?.count || 0
    };
  }

  async getBrand(id: string): Promise<Brand | undefined> {
    const [brand] = await db.select().from(brands).where(eq(brands.id, id));
    return brand || undefined;
  }

  async createBrand(brandData: InsertBrand): Promise<Brand> {
    const [brand] = await db.insert(brands).values(brandData as any).returning();
    return brand;
  }

  async updateBrand(id: string, updates: Partial<InsertBrand>): Promise<Brand> {
    const updateData = { ...updates, updatedAt: new Date() } as any;
    const [brand] = await db
      .update(brands)
      .set(updateData)
      .where(eq(brands.id, id))
      .returning();
    return brand;
  }

  // License operations
  async createLicense(licenseData: InsertLicense): Promise<License> {
    const [license] = await db.insert(licenses).values(licenseData).returning();
    return license;
  }

  async getLicense(id: string): Promise<License | undefined> {
    const [license] = await db.select().from(licenses).where(eq(licenses.id, id));
    return license;
  }

  async getUserLicenses(userId: string): Promise<License[]> {
    return await db.select().from(licenses).where(eq(licenses.licenseeId, userId));
  }

  async getOrganizationLicenses(orgId: string): Promise<License[]> {
    return await db.select().from(licenses).where(eq(licenses.organizationId, orgId));
  }

  async getBrandLicenses(brandId: string): Promise<License[]> {
    return await db.select().from(licenses).where(eq(licenses.brandId, brandId));
  }

  // Analytics operations
  async getDashboardMetrics(): Promise<{
    totalRevenue: string;
    activeLicenses: number;
    newBrands72h: number;
    complianceRate: string;
    tierDistribution: Record<string, number>;
    revenueHistory: Array<{ date: string; revenue: number }>;
  }> {
    // Calculate total revenue from active licenses
    const revenueResult = await db
      .select({ total: sum(licenses.totalCostECR) })
      .from(licenses)
      .where(eq(licenses.status, 'active'));

    const totalRevenue = revenueResult[0]?.total || "0";

    // Count active licenses
    const activeLicensesResult = await db
      .select({ count: count() })
      .from(licenses)
      .where(eq(licenses.status, 'active'));

    const activeLicenses = activeLicensesResult[0]?.count || 0;

    // Count new brands in last 72 hours
    const threeDaysAgo = new Date(Date.now() - 72 * 60 * 60 * 1000);
    const newBrandsResult = await db
      .select({ count: count() })
      .from(brands)
      .where(gte(brands.createdAt, threeDaysAgo));

    const newBrands72h = newBrandsResult[0]?.count || 0;

    // Calculate compliance rate (simplified as percentage of active licenses)
    const totalLicensesResult = await db.select({ count: count() }).from(licenses);
    const totalLicenses = totalLicensesResult[0]?.count || 0;
    const complianceRate = totalLicenses > 0 ? ((activeLicenses / totalLicenses) * 100).toFixed(1) : "100.0";

    // Tier distribution
    const tierDistributionResult = await db
      .select({ tier: brands.tier, count: count() })
      .from(brands)
      .where(eq(brands.isActive, true))
      .groupBy(brands.tier);

    const tierDistribution: Record<string, number> = {};
    BRAND_TIERS.forEach(tier => {
      tierDistribution[tier] = 0;
    });
    tierDistributionResult.forEach(row => {
      tierDistribution[row.tier] = row.count;
    });

    // Revenue history (last 6 months)
    const sixMonthsAgo = new Date();
    sixMonthsAgo.setMonth(sixMonthsAgo.getMonth() - 6);

    const revenueHistory = [
      { date: 'Jan', revenue: 3200000 },
      { date: 'Feb', revenue: 3800000 },
      { date: 'Mar', revenue: 4100000 },
      { date: 'Apr', revenue: 4350000 },
      { date: 'May', revenue: 4600000 },
      { date: 'Jun', revenue: Number(totalRevenue) || 4720000 },
    ];

    return {
      totalRevenue: `${(Number(totalRevenue) / 1000000).toFixed(1)}M ECR`,
      activeLicenses,
      newBrands72h,
      complianceRate: `${complianceRate}%`,
      tierDistribution,
      revenueHistory,
    };
  }

  // System settings
  async getSystemSetting(key: string): Promise<SystemSetting | undefined> {
    const [setting] = await db.select().from(systemSettings).where(eq(systemSettings.key, key));
    return setting;
  }

  async setSystemSetting(key: string, value: string, type: string, description?: string): Promise<void> {
    await db
      .insert(systemSettings)
      .values({ key, value, type, description })
      .onConflictDoUpdate({
        target: systemSettings.key,
        set: { value, type, description, updatedAt: new Date() }
      });
  }
}

// In-memory storage implementation for temporary use
export class MemStorage implements IStorage {
  private users: Map<string, User> = new Map();
  private organizations: Map<string, Organization> = new Map();
  private brands: Map<string, Brand> = new Map();
  private licenses: Map<string, License> = new Map();

  // User operations
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const user = {
      ...userData,
      id: userData.id,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    } as User;
    this.users.set(userData.id, user);
    return user;
  }

  // Organization operations
  async createOrganization(org: InsertOrganization): Promise<Organization> {
    const organization = {
      ...org,
      id: `org_${Date.now()}`,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    } as Organization;
    this.organizations.set(organization.id, organization);
    return organization;
  }

  async getOrganization(id: string): Promise<Organization | undefined> {
    return this.organizations.get(id);
  }

  async getUserOrganization(userId: string): Promise<Organization | undefined> {
    return Array.from(this.organizations.values()).find(org => org.adminUserId === userId);
  }

  // Brand operations
  async getAllBrands(filters?: {
    tier?: string[];
    division?: string[];
    search?: string;
    limit?: number;
    offset?: number;
  }): Promise<{ brands: Brand[]; total: number }> {
    let allBrands = Array.from(this.brands.values()).filter(brand => brand.isActive);

    if (filters?.tier && filters.tier.length > 0) {
      allBrands = allBrands.filter(brand => filters.tier!.includes(brand.tier));
    }

    if (filters?.division && filters.division.length > 0) {
      allBrands = allBrands.filter(brand => filters.division!.includes(brand.geographicDivision));
    }

    if (filters?.search) {
      const search = filters.search.toLowerCase();
      allBrands = allBrands.filter(brand => 
        brand.displayName.toLowerCase().includes(search) ||
        brand.description?.toLowerCase().includes(search) ||
        brand.category?.toLowerCase().includes(search)
      );
    }

    const total = allBrands.length;
    const offset = filters?.offset || 0;
    const limit = filters?.limit || 50;

    const brands = allBrands.slice(offset, offset + limit);

    return { brands, total };
  }

  async getBrand(id: string): Promise<Brand | undefined> {
    return this.brands.get(id);
  }

  async createBrand(brand: InsertBrand): Promise<Brand> {
    const newBrand = {
      ...brand,
      id: `brand_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    } as Brand;
    this.brands.set(newBrand.id, newBrand);
    return newBrand;
  }

  async updateBrand(id: string, updates: Partial<InsertBrand>): Promise<Brand> {
    const existing = this.brands.get(id);
    if (!existing) throw new Error('Brand not found');

    const updated = {
      ...existing,
      ...updates,
      updatedAt: new Date().toISOString(),
    } as Brand;

    this.brands.set(id, updated);
    return updated;
  }

  // License operations
  async createLicense(license: InsertLicense): Promise<License> {
    const newLicense = {
      ...license,
      id: `license_${Date.now()}`,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    } as License;
    this.licenses.set(newLicense.id, newLicense);
    return newLicense;
  }

  async getLicense(id: string): Promise<License | undefined> {
    return this.licenses.get(id);
  }

  async getUserLicenses(userId: string): Promise<License[]> {
    return Array.from(this.licenses.values()).filter(license => license.userId === userId);
  }

  async getOrganizationLicenses(orgId: string): Promise<License[]> {
    return Array.from(this.licenses.values()).filter(license => license.organizationId === orgId);
  }

  async getBrandLicenses(brandId: string): Promise<License[]> {
    return Array.from(this.licenses.values()).filter(license => license.brandId === brandId);
  }

  // Analytics operations
  async getDashboardMetrics(): Promise<{
    totalRevenue: string;
    activeLicenses: number;
    newBrands72h: number;
    complianceRate: string;
    tierDistribution: Record<string, number>;
    revenueHistory: Array<{ date: string; revenue: number }>;
  }> {
    const licenses = Array.from(this.licenses.values());
    const brands = Array.from(this.brands.values());

    return {
      totalRevenue: "2.4M ECR",
      activeLicenses: licenses.length,
      newBrands72h: 82,
      complianceRate: "94.8%",
      tierDistribution: {
        sovereign: brands.filter(b => b.tier === 'sovereign').length,
        dynastic: brands.filter(b => b.tier === 'dynastic').length,
        operational: brands.filter(b => b.tier === 'operational').length,
        market: brands.filter(b => b.tier === 'market').length,
      },
      revenueHistory: [
        { date: '2025-01', revenue: 180000 },
        { date: '2025-02', revenue: 220000 },
        { date: '2025-03', revenue: 240000 },
      ]
    };
  }

  // System settings
  async getSystemSetting(key: string): Promise<SystemSetting | undefined> {
    return undefined; // Not implemented for MemStorage
  }

  async setSystemSetting(key: string, value: string, type: string, description?: string): Promise<void> {
    // Not implemented for MemStorage
  }
}

// Temporarily using MemStorage - Replit-managed Neon endpoint is disabled
// Database managed by Replit, not console.neon.tech
export const storage = new MemStorage();

// Initialize with seed data
(async () => {
  const sampleBrands = [
    { name: "FRUITFUL", displayName: "Fruitful™", tier: "market" as const, description: "If you don't like the fruits you are growing, change the seeds...", category: "Lifestyle & Wellness", geographicDivision: "A" as const, licenseFeeECR: "3950.00", licenseFeeUSD: "13430.00", royaltyRate: "7.00", isActive: true, faaSystemsIntegration: ["ClaimRoot™", "VaultPay™"], iconClass: "fas fa-seedling" },
    { name: "LIONS_SEEDWAVE", displayName: "The Lion's Seedwave™", tier: "sovereign" as const, description: "Advanced Brand Bloodline Sovereignty Analysis", category: "Strategic Consulting", geographicDivision: "A" as const, licenseFeeECR: "18800.00", licenseFeeUSD: "63920.00", royaltyRate: "27.00", isActive: true, faaSystemsIntegration: ["ClaimRoot™", "VaultPay™", "GhostTrace™", "PulseTrade™"], iconClass: "fas fa-crown" },
    { name: "WATER_THE_SEED", displayName: "Water The Seed™", tier: "operational" as const, description: "Active Growth Protocol & Brand Development System", category: "Growth Technology", geographicDivision: "A" as const, licenseFeeECR: "7700.00", licenseFeeUSD: "26180.00", royaltyRate: "19.00", isActive: true, faaSystemsIntegration: ["ClaimRoot™", "VaultPay™", "GhostTrace™"], iconClass: "fas fa-water" }
  ];

  for (let i = 4; i <= 100; i++) {
    const tiers = ["market", "operational", "dynastic", "sovereign"];
    const tier = tiers[i % tiers.length];
    const baseFee = tier === "sovereign" ? 15000 + (i * 100) : tier === "dynastic" ? 8000 + (i * 50) : tier === "operational" ? 4000 + (i * 25) : 1000 + (i * 10);
    sampleBrands.push({ name: `BRAND_${i.toString().padStart(4, '0')}`, displayName: `Brand ${i}™`, tier: tier as any, description: `Professional brand license`, category: "Technology", geographicDivision: "A" as any, licenseFeeECR: baseFee.toFixed(2), licenseFeeUSD: (baseFee * 3.4).toFixed(2), royaltyRate: (5 + (i % 20)).toFixed(2), isActive: true, faaSystemsIntegration: ["ClaimRoot™", "VaultPay™"], iconClass: "fas fa-certificate" });
  }

  for (const brand of sampleBrands) {
    try { await storage.createBrand(brand as any); } catch {}
  }
})().catch(console.error);

