export async function seedHSOMNI9000Data() {
  console.log('🚀 Starting HSOMNI9000 complete data import...');
  console.log('📦 Source: FruitfulPlanetChange GitHub repository');

  try {
    // Clear existing HSOMNI data
    console.log('🧹 Clearing existing HSOMNI data...');
    await db.delete(hsomniAdminPanelBrands);
    await db.delete(hsomniSectors);

    let totalSectors = 0;
    let totalBrands = 0;

    // First, create all sectors
    console.log('🌐 Creating sectors...');
    for (const [sectorKey, sectorData] of Object.entries(HSOMNI_SECTOR_DATA)) {
      const { sectorName, sectorEmoji, brands, subNodes } = sectorData;

      await db.insert(hsomniSectors).values({
        sectorKey,
        name: sectorName,
        emoji: sectorEmoji,
        description: `${sectorName} ecosystem with ${brands.length} brands`,
        brandCount: brands.length,
        subnodeCount: subNodes.length,
        price: "88.00",
        currency: "USD",
        metadata: { subNodes, source: "FruitfulPlanetChange" }
      });

      totalSectors++;
    }

    // Then import all brands
    console.log('🔄 Importing all brands...');
    for (const [sectorKey, sectorData] of Object.entries(HSOMNI_SECTOR_DATA)) {
      const { sectorName, sectorEmoji, brands, subNodes } = sectorData;

      console.log(`  📊 ${sectorName}: ${brands.length} brands`);

      const uniqueBrands = [...new Set(brands)];
      for (const brandName of uniqueBrands) {
        await db.insert(hsomniAdminPanelBrands).values({
          sectorKey,
          sectorName,
          sectorEmoji,
          brandName: brandName + '™',
          subNodes,
          metadata: {
            tier: ['banking', 'ai-logic', 'quantum'].includes(sectorKey) ? 'A+' : 
                  ['creative', 'gaming', 'saas'].includes(sectorKey) ? 'A' : 'B+',
            region: 'Global',
            pricing: '$88',
            status: 'active',
            source: 'FruitfulPlanetChange'
          },
          status: 'active',
          isCore: ['banking', 'ai-logic', 'quantum'].includes(sectorKey)
        });
        totalBrands++;
      }
    }

    console.log('');
    console.log('🎉 HSOMNI9000 DATA IMPORT COMPLETE!');
    console.log('═══════════════════════════════════');
    console.log(`✅ Total Sectors Imported: ${totalSectors}`);
    console.log(`✅ Total Brands Imported: ${totalBrands}`);
    console.log(`📁 Source: FruitfulPlanetChange repository (836 files)`);
    console.log(`🗄️  Database Tables: hsomni_sectors, hsomni_admin_panel_brands`);
    console.log('═══════════════════════════════════');

    return { success: true, totalSectors, totalBrands };

  } catch (error) {
    console.error('💥 HSOMNI9000 import failed:', error);
    throw error;
  }
}

// Auto-execute if run directly
if (import.meta.url === `file://${process.argv[1]}`) {
  seedHSOMNI9000Data()
    .then((result) => {
      console.log('✅ Import completed:', result);
      process.exit(0);
    })
    .catch((error) => {
      console.error('❌ Import failed:', error);
      process.exit(1);
    });
}

server/validate-healthtrack-alignment.ts.content:
import { db } from './db';
import { hsomniBrands, hsomniSectors } from '@shared/schema';
import { eq, and, sql } from 'drizzle-orm';

export async function validateHealthTrackAlignment(): Promise<boolean> {
  try {
    const [healthSector] = await db.select().from(hsomniSectors).where(eq(hsomniSectors.sectorKey, 'health'));

    if (!healthSector) {
      console.error('❌ Health sector not found');
      return false;
    }

    const brandCounts = await db.select({
      total: sql<number>`COUNT(*)`,
      cores: sql<number>`COUNT(CASE WHEN ${hsomniBrands.isCore} THEN 1 END)`,
      subnodes: sql<number>`COUNT(CASE WHEN NOT ${hsomniBrands.isCore} THEN 1 END)`,
      fruitfulCount: sql<number>`COUNT(CASE WHEN ${hsomniBrands.metadata}->>'source' = 'FruitfulPlanetChange' THEN 1 END)`
    }).from(hsomniBrands).where(eq(hsomniBrands.sectorId, healthSector.id));

    const counts = brandCounts[0];

    // Convert to numbers since SQL may return strings
    const total = Number(counts.total);
    const cores = Number(counts.cores);
    const subnodes = Number(counts.subnodes);
    const fruitfulCount = Number(counts.fruitfulCount);

    const isValid = 
      total === 465 &&
      cores === 93 &&
      subnodes === 372 &&
      fruitfulCount === 465;

    if (isValid) {
      console.log('✅ HealthTrack alignment validated:');
      console.log(`   - Total brands: ${total} (expected: 465)`);
      console.log(`   - CORE brands: ${cores} (expected: 93)`);
      console.log(`   - Subnodes: ${subnodes} (expected: 372)`);
      console.log(`   - FruitfulPlanetChange source: ${fruitfulCount} (expected: 465)`);
    } else {
      console.error('❌ HealthTrack alignment validation FAILED:');
      console.error(`   - Total: ${total} (expected: 465)`);
      console.error(`   - CORE: ${cores} (expected: 93)`);
      console.error(`   - Subnodes: ${subnodes} (expected: 372)`);
      console.error(`   - FruitfulPlanetChange: ${fruitfulCount} (expected: 465)`);
    }

    return isValid;
  } catch (error) {
    console.error('❌ Validation error:', error);
    return false;
  }
}

// Run validation when executed directly
validateHealthTrackAlignment().then(valid => {
  if (import.meta.url === `file://${process.argv[1]}`) {
    process.exit(valid ? 0 : 1);
  }
});

